import React, { useState} from "react"

const AddRestaurant = () => {
  const [formData, setFormData] = useState({
    restaurantName: "",
    restaurantAddress: "",
    restaurantAddedByEmail: "",
  });

  const [formErrors, setFormErrors] = useState({
    restaurantName: "",
    restaurantAddress: "",
    restaurantAddedByEmail: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;

    // Update form data
    setFormData({
      ...formData,
      [name]: value,
    });

    // Perform validation
    if (name === "restaurantName" && value === "") {
      setFormErrors({
        ...formErrors,
        restaurantName: "Restaurant name is required.",
      });
    } else if (name === "restaurantAddress" && value === "") {
      setFormErrors({
        ...formErrors,
        restaurantAddress: "Restaurant address is required.",
      });
    } else if (name === "restaurantAddedByEmail" && !/^\S+@\S+\.\S+$/.test(value)) {
      setFormErrors({
        ...formErrors,
        restaurantAddedByEmail: "Invalid email address.",
      });
    } else {
      // Clear validation errors if input is valid
      setFormErrors({
        ...formErrors,
        [name]: "",
      });
    }
  };

  const HandleSubmit = (event) => {
    //event.preventDefault();

    // Perform validation before submitting the form
    const validationErrors = Object.keys(formData).reduce((errors, name) => {
      if (formData[name] === "") {
        errors[name] = `${
          name.charAt(0).toUpperCase() + name.slice(1)
        } is required.`;
      } else if (name === "restaurantAddedByEmail" && !/^\S+@\S+\.\S+$/.test(formData[name])) {
        errors[name] = "Invalid email address.";
      }
      return errors;
    }, {});

    // Update form errors
    setFormErrors(validationErrors);

    // Check if there are any validation errors
    if (Object.values(validationErrors).every((error) => error === "")) {
      // Perform custom business logic or submit the form
      try {
        axios.post('http://localhost:8080/restaurant/api/v1/addUser', {
            userFirstName: formData.firstName,
            userLastName: formData.lastName,
            userEmailAddress: formData.email
          })
    .then(res=>{
      console.log(res);
      console.log(res.data);
    })
      } catch (error) {
        console.error("Error fetching data:", error);
        window.location = "/error" //This line of code will redirect you once the submission is succeed
      }

      return alert("Restaurant Added Successfully");
      console.log("Form submitted successfully!");
      console.log("Form Data:", formData);
    } else {
      console.log("Form validation failed. Please check the errors.");
    }
  };

  return (
    <form>
      <label>
      Restaurant Name:
        <input type="text" name="restaurantName" value={formData.restaurantName} onChange={handleInputChange}/>
        <span className="error">{formErrors.restaurantName}</span>
      </label>

      <label>
      Restaurant Address:
        <input type="text" name="restaurantAddress" value={formData.restaurantAddress} onChange={handleInputChange}/>
        <span className="error">{formErrors.restaurantAddress}</span>
      </label>

      <label>
      Restaurant Added By Email:
        <input type="email" name="restaurantAddedByEmail" value={formData.restaurantAddedByEmail} onChange={handleInputChange} />
        <span className="error">{formErrors.restaurantAddedByEmail}</span>
      </label>
      <button type="submit" onClick={HandleSubmit}>Submit</button>
    </form>
  );

};

export default AddRestaurant;
