import React, { useState} from "react"

const AddUser = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
  });

  const [formErrors, setFormErrors] = useState({
    firstName: "",
    lastName: "",
    email: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;

    // Update form data
    setFormData({
      ...formData,
      [name]: value,
    });

    // Perform validation
    if (name === "firstName" && value === "") {
      setFormErrors({
        ...formErrors,
        firstName: "First name is required.",
      });
    } else if (name === "lastName" && value === "") {
      setFormErrors({
        ...formErrors,
        lastName: "Last name is required.",
      });
    } else if (name === "email" && !/^\S+@\S+\.\S+$/.test(value)) {
      setFormErrors({
        ...formErrors,
        email: "Invalid email address.",
      });
    } else {
      // Clear validation errors if input is valid
      setFormErrors({
        ...formErrors,
        [name]: "",
      });
    }
  };

  const HandleSubmit = (event) => {
    //event.preventDefault();

    // Perform validation before submitting the form
    const validationErrors = Object.keys(formData).reduce((errors, name) => {
      if (formData[name] === "") {
        errors[name] = `${
          name.charAt(0).toUpperCase() + name.slice(1)
        } is required.`;
      } else if (name === "email" && !/^\S+@\S+\.\S+$/.test(formData[name])) {
        errors[name] = "Invalid email address.";
      }
      return errors;
    }, {});

    // Update form errors
    setFormErrors(validationErrors);

    // Check if there are any validation errors
    if (Object.values(validationErrors).every((error) => error === "")) {
      // Perform custom business logic or submit the form


      const user = {
        userFirstName: this.state.firstName,
        userLastName: this.state.lastName,
        userEmailAddress: this.state.email
      }
      axios.post('https://jsonplaceholder.typicode.com/users', { user })
        .then(res=>{
          console.log(res);
          console.log(res.data);
          //window.location = "/retrieve" //This line of code will redirect you once the submission is succeed
        })

      return alert("User Added Successfully");
    } else {
      console.log("Form validation failed. Please check the errors.");
    }

  };


  return (
    <form>
      <label>
        First Name:
        <input type="text" name="firstName" value={formData.firstName} onChange={handleInputChange}/>
        <span className="error">{formErrors.firstName}</span>
      </label>

      <label>
        Last Name:
        <input type="text" name="lastName" value={formData.lastName} onChange={handleInputChange}/>
        <span className="error">{formErrors.lastName}</span>
      </label>

      <label>
        Email:
        <input type="email" name="email" value={formData.email} onChange={handleInputChange} />
        <span className="error">{formErrors.email}</span>
      </label>
      <button type="submit" onClick={HandleSubmit}>Submit</button>
    </form>
  );

};

export default AddUser;
